import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    // Dummy login (no backend auth needed)
    navigate("/dashboard");
  };

  return (
    <div style={{
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      height: "100vh",
      backgroundColor: "#0b0c10",
    }}>
      <div style={{
        textAlign: "center",
        border: "2px solid black",
        display: "flex",
        flexDirection: "column",
        padding: "20px",
        width: "40vh",
        backgroundColor: "#1f2833",
        boxShadow: "0px 0px 10px 10px #66fcf1",
        borderRadius: "10px",
        color: "#66fcf1",
        
      }}>
        <h1>Intern Portal ðŸ”‘</h1>

        {/* âœ… Separator Line */}
        <div
          style={{
            width: "100%",
            borderTop: "2px solid #66fcf1",
            marginTop: "15px",
            paddingTop: "10px",
            textAlign: "center",
          }}
        >
          <h2>Login</h2>
        </div>

        <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "15px", marginTop: "20px" }}>
          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            style={{
              padding: "10px",
              borderRadius: "5px",
              border: "1px solid #66fcf1",
              backgroundColor: "#0b0c10",
              color: "#66fcf1",
            }}
          />
          <input
            type="password"
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            style={{
              padding: "10px",
              borderRadius: "5px",
              border: "1px solid #66fcf1",
              backgroundColor: "#0b0c10",
              color: "#66fcf1",
            }}
          />
          <button
            type="submit"
            style={{
              padding: "12px",
              backgroundColor: "#66fcf1",
              color: "#0b0c10",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
              fontSize: "16px",
              fontWeight: "bold",
            }}
          >
            Login ðŸš€
          </button>
        </form>
      </div>
    </div>
  );
}

export default Login;
