import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

function Leaderboard() {
  const [leaders, setLeaders] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch("http://localhost:5000/api/leaderboard")
      .then((res) => res.json())
      .then((data) => setLeaders(data));
  }, []);

  return (
    <div style={{
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      height: "100vh",
      backgroundColor: "#0b0c10",
    }}>
      <div style={{
        textAlign: "center",
        border: "2px solid black",
        display: "flex",
        flexDirection: "column",
        padding: "20px",
        width: "70vh",
        backgroundColor: "#1f2833",
        boxShadow: "0px 0px 10px 10px #66fcf1",
        borderRadius: "10px",
        color: "#66fcf1",
      }}>
        <h1>Leaderboard üèÜ</h1>

        {/* ‚úÖ Separator line */}
        <div
          style={{
            width: "100%",
            borderTop: "2px solid #66fcf1",
            marginTop: "15px",
            paddingTop: "10px",
            textAlign: "center",
          }}
        >
          <h2>Top Performers</h2>
        </div>

        <table style={{
          margin: "20px auto",
          width: "100%",
          borderCollapse: "collapse",
          color: "#66fcf1",
        }}>
          <thead>
            <tr style={{ backgroundColor: "#0b0c10" }}>
              <th style={{ border: "1px solid #66fcf1", padding: "10px" }}>Name</th>
              <th style={{ border: "1px solid #66fcf1", padding: "10px" }}>Donations</th>
            </tr>
          </thead>
          <tbody>
            {leaders.map((leader, i) => (
              <tr key={i}>
                <td style={{ border: "1px solid #66fcf1", padding: "10px" }}>{leader.name}</td>
                <td style={{ border: "1px solid #66fcf1", padding: "10px" }}>‚Çπ{leader.donations}</td>
              </tr>
            ))}
          </tbody>
        </table>

        {/* ‚úÖ Back Button */}
        <button
          onClick={() => navigate("/dashboard")}
          style={{
            marginTop: "20px",
            padding: "12px",
            width: "100%",
            backgroundColor: "#007bff",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
            fontSize: "16px",
            fontWeight: "bold",
          }}
        >
          Back to Dashboard üîô
        </button>
      </div>
    </div>
  );
}

export default Leaderboard;
