import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom"; // ‚úÖ Import for navigation

function Dashboard() {
  const [user, setUser] = useState(null);
  const navigate = useNavigate(); // ‚úÖ Hook for redirect

  useEffect(() => {
    fetch("http://localhost:5000/api/user")
      .then((res) => res.json())
      .then((data) => setUser(data));
  }, []);

  if (!user) {
    return (
      <div style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "100vh",
      }}>
        <p>Loading...</p>
      </div>
    );
  }

  return (
    <div style={{
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      height: "100vh",
    }}>
      <div style={{
        textAlign: "center",
        border: "2px solid black",
        display: "flex",
        flexDirection: "column",
        padding: "20px",
        width: "50vh",
        backgroundColor: "off-white",
        boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
        borderRadius: "10px",
        color:""
      }}>
        <h1>Welcome, {user.name} üéâ</h1>
        <p>Referral Code: <b>{user.referralCode}</b></p>
        <p>Total Donations: <b>‚Çπ{user.totalDonations}</b></p>
        <h2 style={{ borderTop: "2px solid black", paddingTop: "10px" }}>Rewards</h2>
        <ul style={{ listStyleType: "none", padding: 0 }}>
          {user.rewards.map((reward, index) => (
            <li key={index}>{reward}</li>
          ))}
        </ul>

        {/* ‚úÖ Leaderboard Button */}
        <button
          onClick={() => navigate("/leaderboard")}
          style={{
            marginTop: "20px",
            padding: "10px 20px",
            backgroundColor: "#007bff",
            color: "white",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
          }}
        >
          View Leaderboard üèÜ
        </button>
      </div>
    </div>
  );
}

export default Dashboard;
