import React, { useState , useEffect } from "react";

function Dashboard() {
    const [user, setUser] = useState(null);

    useEffect(() => {
    fetch("http://localhost:3000/api/user")
    .then(response => { response.json() })
    .then(data => {  setUser(data) })
    }, []);

    if (!user) { 
        return <p>Loading...</p>;
    }

    return(
        <div className="p-6 text-center">
      <h1 className="text-3xl font-bold">Welcome, {user.name} ðŸŽ‰</h1>
      <p>Referral Code: <b>{user.referralCode}</b></p>
      <p>Total Donations Raised: <b>â‚¹{user.totalDonations}</b></p>
      
      <h2 className="text-2xl mt-4">Rewards</h2>
      <ul>
        {user.rewards.map((reward, i) => (
          <li key={i} className="mt-2">{reward}</li>
        ))}
      </ul>
    </div>
  );
}

export default Dashboard;
