import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    // Dummy login (no backend auth needed)
    navigate("/dashboard");
  };

  return (
    <div style={{
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
      height: "100vh",
    }}>
      <div style={{
        backdropFilter: "blur(10px)",
        background: "rgba(255, 255, 255, 0.15)", // Semi-transparent glass
        border: "2px solid #0a46aa",
        borderRadius: "15px",
        padding: "30px",
        width: "350px",
        boxShadow: "0 8px 32px rgba(0, 0, 0, 0.3)",
        textAlign: "center",
        color: "#fff",
      }}>
        <h1 style={{ marginBottom: "20px" }}>Intern Portal ðŸ”‘</h1>
        
        <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "15px" }}>
          <input
            type="email"
            placeholder="Enter your email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            style={{
              padding: "12px",
              borderRadius: "8px",
              border: "none",
              outline: "none",
              background: "rgba(255, 255, 255, 0.2)",
              color: "#fff",
              fontSize: "14px",
            }}
            onMouseOver={(e) => e.target.style.border = "#0a46aa"}
            onMouseOut={(e) => e.target.style.background = "rgba(255, 255, 255, 0.3)"}
          />
          <input
            type="password"
            placeholder="Enter your password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            style={{
              padding: "12px",
              borderRadius: "8px",
              border: "none",
              outline: "none",
              background: "rgba(255, 255, 255, 0.2)",
              color: "#fff",
              fontSize: "14px",
            }}
            onMouseOver={(e) => e.target.style.background = "rgba(255, 255, 255, 0.5)"}
            onMouseOut={(e) => e.target.style.background = "rgba(255, 255, 255, 0.3)"}
          />
          <button
            type="submit"
            style={{
              padding: "12px",
              background: "rgba(255, 255, 255, 0.3)",
              border: "none",
              borderRadius: "8px",
              color: "#fff",
              fontWeight: "bold",
              cursor: "pointer",
              transition: "0.3s",
            }}
            onMouseOver={(e) => e.target.style.background = "rgba(255, 255, 255, 0.5)"}
            onMouseOut={(e) => e.target.style.background = "rgba(255, 255, 255, 0.3)"}
          >
            Login ðŸš€
          </button>
        </form>
      </div>
    </div>
  );
}

export default Login;
