import React, { useEffect, useState } from "react";

function Dashboard() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    fetch("http://localhost:5000/api/user")
      .then((res) => res.json())
      .then((data) => setUser(data));
  }, []);

  if (!user) return <p style={{ textAlign: "center" }}>Loading...</p>;

  return (
    <div style={{ textAlign: "center", marginTop: "50px", border: "2px solid black", display: "inline-block", padding: "20px" }}>
      <h1>Welcome, {user.name} ðŸŽ‰</h1>
      <p>Referral Code: <b>{user.referralCode}</b></p>
      <p>Total Donations: <b>â‚¹{user.totalDonations}</b></p>
      <h2 style={{ borderTop: "2px solid black", paddingTop: "10px" }}>Rewards</h2>
      <ul style={{ listStyleType: "none", padding: 0 }}>
        {user.rewards.map((reward, index) => (
          <li key={index}>{reward}</li>
        ))}
      </ul>
    </div>
  );
}

export default Dashboard;
